<script>
import catalogItem from './components/catalogItem'
import cartItem from './components/cartItem'

export default {
data() {
    return {
	products: [
		{
			id: Math.random().toString().slice(2,8),
			title: 'Футболки',
			price: 10,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'Майки',
			price: 70,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'Джинсы',
			price: 22,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'с принтами',
			price: 10,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'Майки',
			price: 70,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'Джинсы',
			price: 22,
			quantity: 1,
			isOnCart: false
		},
		{
			id: Math.random().toString().slice(2,8),
			title: 'Джинсы',
			price: 22,
			quantity: 1,
			isOnCart: false
		}
	],
	cart: [],
	bascets: [
		{
			title: 'Coments'
		},
		{
			title: 'isolation'
		},
		{
			title: 'endodontic material'
		},
		{
			title: 'Acrilyc resins'
		},
		{
			title: 'Topical anestethics'
		},
		{
			title: 'lorem'
		},
		{
			title: 'lorem'
		},
		{
			title: 'Temporerry fillings '
		}
	]
	}
},

computed: {
	cartTotalCost() {
		let result = []

		if (this.cart.length) {
			for(let item of this.cart) {
				result.push(item.price * item.quantity)
			}
	
			result = result.reduce(function (sum, el){
				return sum + el
			})
			return result
		} else {
			return 0
		}
	},
	
},

methods: {
	pushToCart(data) {
		let filtered = this.products.filter(product => product.id == data)[0]
		filtered.isOnCart = true
		this.cart.push(filtered)
	},
	deleteproduct(itemId) {
		let deleted = this.cart.filter(item => item.id == itemId)[0]
		this.cart.splice(this.cart.indexOf(deleted), 1)


	},
	changeCount(data) {
		let filtered = this.products.filter(product => product.id == data.id)[0]
		if(data.data) filtered.quantity++
		else if (filtered.quantity != 0) filtered.quantity--
	}
},

components: {
	catalogItem,
	cartItem
}
}
</script>

